var Idle = context.get('Idle')||0;
var Audio = context.get('Audio')||0;
var Notify = context.get('Notify')||0;
var UI = context.get('UI')||0;

if (msg.topic === "Alarm/MODE"){
    if(msg.payload.includes("Idle")){
        context.set('Idle',1);    
        context.set('Audio',0);
        context.set('Notify',0);
    }else if(msg.payload.includes("Audio")){
        context.set('Idle',0);
        context.set('Audio',1);
        context.set('Notify',0);
    }else if(msg.payload.includes("Notify")){
        context.set('Idle',0);
        context.set('Audio',0);
        context.set('Notify',1);
    }
    return [ msg, null, null, null ];
}

if (msg.topic.includes("Alarm/UImode")){
    if(msg.payload.includes("3"))
        context.set('UI', 3);
    if(msg.payload.includes("2"))
        context.set('UI', 2);
    if(msg.payload.includes("1"))
        context.set('UI', 1);
    if(msg.payload.includes("0"))
        context.set('UI', 0);
    return [ null, null, null, null ];
}

if (msg._topic.includes("ImageBuffer")){
    if (UI == 3  && msg._topic.includes("Cam")){
        return [ null, null, null, null ]; // AIDetection image will show it
    }
    else{
        msg.UI = context.get('UI');
        return [ null, null, null, msg ]; // this handles "live view" and detections for a specified camera
    }
}



/* LorexAI "in alert region" filter.
 Install point-in-polygon with:
    cd ~/.node-red
    npm install point-in-polygon
 var inside = require('point-in-polygon'); // doesn't work in node-red.
 So must edit .node-red/settings.js and add:
      functionGlobalContext: {
          insidePolygon:require('point-in-polygon')
      }
 Requires a node-red restart when finished.
*/

var inside = global.get('insidePolygon');
msg.InAlertRegion=true;   // default is to accept detection anywhere in image

// screen for LR box point in region
var poly=[[]];

// polygon points picked from image using GIMP
/* Want full frame detections
if(msg.filename.includes("Cam0"))
    poly=[ [0,0], [0,350], [1301,370], [1301,0] ];
else if(msg.filename.includes("Cam1"))
    poly=[ [0,0], [0,400], [1150,400], [1150,0] ];
else if(msg.filename.includes("Cam2"))
    poly=[ [10,260], [10,465], [1000,460], [1190,360], [1190,235], [680,220], [530,230] ];
else if(msg.filename.includes("Cam2"))
    poly=[ [0,0], [2580,0], [2580,730], [0,730] ];
*/

if(poly.length > 2)
    msg.InAlertRegion = inside([msg.startX, msg.startY], poly); // returns false if point not inside polygon

// filter for box points matching bogus detection of static object
// var bogus=[[ ULx.ULy, LRx,LRy, tol ]];
var bogus= [ [] ];  // empty 2D array is 1 by 0

// UL,LR of reject object obtained from bogus AI detection image overlay text
// CamBogus = [ [ startX,startY, endX,endY, tolerence] ]; // multiple bogus detections can be in a frame, so 2D array, N by 5.
/*
if(msg.filename.includes("PoolEquipment"))
    bogus=[ [1337,658, 1414,756, 0.5],
            [659,486, 1094,1063, 0.2] ]; // pool plumbing pipes, filter tank
else if(msg.filename.includes("HummingbirdLeft"))
    bogus=[ [2918,235, 3343,916, 0.3] ];    // tree trunk, so far only see these with CPU or NCS AI
else if(msg.filename.includes("MailBox"))
    bogus=[ [883,117, 1606,122, 0.2] ];    // morning shadows on mailbox
else if(msg.filename.includes("SideYard"))
    bogus=[ [864,136, 864,674, 0.2] ];    // afternoon shadows on tarp, so far only see these with CPU or NCS AI
*/

if(bogus[0].length == 5){
    for (var i=0; i<bogus.length; i++){
        xtol=(bogus[i][2]-bogus[i][0])*bogus[i][4];
        if(Math.abs(msg.startX-bogus[i][0])>xtol){continue}
        if(Math.abs(msg.endX-bogus[i][2])>xtol){continue}
        ytol=(bogus[i][3]-bogus[i][1])*bogus[i][4];
        if(Math.abs(msg.startY-bogus[i][1])>xtol){continue}
        if(Math.abs(msg.endY-bogus[i][3])>xtol){continue}
        msg.InAlertRegion=false;
    }
}


if (msg.topic.includes("AIdetection")){
    if (Idle == 1 || msg.InAlertRegion === false){
        return [ null, null, null, null ];
    }
    if (Audio == 1){
        if(UI == 3)
            return [ null, msg, null, msg ];
        else
            return [ null, msg, null, null ];
    }
    if (Notify == 1){
        if(UI == 3)
            return [ null, msg, msg, msg ];
         else
             return [ null, msg, msg, null ];
   }
}

return [ null, null, null, null ];
